PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE "schema_migrations" ("version" varchar NOT NULL);
CREATE TABLE "funcionarios" ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "matricula" varchar, "nome" varchar, "endereco" varchar, "telefone" varchar, "salario" float, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL, "sexo" varchar, "dtadmissao" date, "foto" varchar, "setor_id" integer, "cargo_id" integer);
CREATE TABLE "setores" ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "codsetor" integer, "nomesetor" varchar, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL, "filial_id" integer);
CREATE TABLE "cargos" ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "codcargo" integer, "nomecargo" varchar, "salariobase" float, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL);
CREATE TABLE "filiais" ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "nomefilial" varchar, "uf" varchar, "telefone" varchar, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL);
CREATE TABLE "usuarios" ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "email" varchar DEFAULT '' NOT NULL, "encrypted_password" varchar DEFAULT '' NOT NULL, "reset_password_token" varchar, "reset_password_sent_at" datetime, "remember_created_at" datetime, "sign_in_count" integer DEFAULT 0 NOT NULL, "current_sign_in_at" datetime, "last_sign_in_at" datetime, "current_sign_in_ip" varchar, "last_sign_in_ip" varchar, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL);
DELETE FROM sqlite_sequence;
CREATE UNIQUE INDEX "unique_schema_migrations" ON "schema_migrations" ("version");
CREATE INDEX "index_funcionarios_on_setor_id" ON "funcionarios" ("setor_id");
CREATE INDEX "index_funcionarios_on_cargo_id" ON "funcionarios" ("cargo_id");
CREATE INDEX "index_setores_on_filial_id" ON "setores" ("filial_id");
CREATE UNIQUE INDEX "index_usuarios_on_email" ON "usuarios" ("email");
CREATE UNIQUE INDEX "index_usuarios_on_reset_password_token" ON "usuarios" ("reset_password_token");
COMMIT;
